    for(int i = 1;i<=n;i++){
        cin>>num[i];
    }
    for(int i = 0;i<=2*k;i++){
        start[i] = 0;
    }
    for(int i = 1;i<=n;i++){
        ll mo  = num[i]%(2*k);
        ll p = mo + k - 1;
        if(p >= 2*k){
            p = p-2*k+1;
            //cout<<p<<' '<<mo<<endl;
            start[p]++;
            start[mo]--;
        }
        else{
            //cout<<0<<' '<<mo<<endl;
            start[0]++;
            start[mo]--;
            if(p+1<=2*k) {
                //cout<<p+1<<' '<<2*k<<endl;
                start[p + 1]++;
                start[2 * k]--;
            }
        }
    }
    ll ma = 1e15;
    sort(num+1,num+1+n);
    int p = 0;
    for(int i = 0;i<2*k;i++){
        p += start[i];
        //cout<<start[i]<<' ';
        if(p==0){
            int p = num[n]%(2*k);
            int cha = (i + 2*k - p)%(2*k);
            ma = min(ma,num[n]+cha);
        }
    }
    cout<<(ma==1e15?-1:ma)<<endl;
}
